import {
  ApplicationRef,
  ChangeDetectorRef,
  ComponentRef,
  Directive,
  ElementRef,
  HostBinding,
  HostListener,
  input,
  InputSignal,
  OnDestroy,
  ViewContainerRef
} from '@angular/core';
import { Clipboard } from '@angular/cdk/clipboard';
import { TooltipComponent } from '../components/tooltip/tooltip.component';
import { TooltipStyle } from '../enums/tooltip.enum';

@Directive({ selector: '[copyToClipboard]', standalone: true })
export class CopyToClipboardDirective implements OnDestroy {
  copyToClipboard: InputSignal<string> = input.required();
  copyToClipboardTooltipShow: InputSignal<boolean> = input(true);
  copyToClipboardTooltipShowDelay: InputSignal<number> = input(0);
  copyToClipboardTooltipHideDelay: InputSignal<number> = input(0);

  copyToClipboardTooltipText: InputSignal<string> = input('Click to Copy!');
  copyToClipboardTooltipSuccess: InputSignal<string> = input('Copied!');
  copyToClipboardTooltipHideAfterSuccessDelay: InputSignal<number> =
    input(1000);

  private componentRef: ComponentRef<TooltipComponent> | null = null;
  private showTimeouts: ReturnType<typeof setTimeout>[] = [];
  private hideTimeouts: ReturnType<typeof setTimeout>[] = [];

  @HostBinding('style.cursor') cursor: string = 'pointer';
  @HostListener('click', ['$event'])
  onClick(): void {
    this.clipboard.copy(this.copyToClipboard());

    if (this.copyToClipboardTooltipShow()) {
      this.initializeTooltip(
        this.copyToClipboardTooltipSuccess(),
        TooltipStyle.Success
      );
      this.setHideTooltipTimeout(
        this.copyToClipboardTooltipHideAfterSuccessDelay()
      );
    }
  }

  @HostListener('mouseenter')
  onMouseEnter(): void {
    if (this.copyToClipboardTooltipShow()) {
      this.initializeTooltip(this.copyToClipboardTooltipText());
    }
  }

  @HostListener('mouseleave')
  onMouseLeave(): void {
    if (this.copyToClipboardTooltipShow()) {
      this.setHideTooltipTimeout(this.copyToClipboardTooltipHideDelay());
    }
  }

  constructor(
    private elementRef: ElementRef,
    private appRef: ApplicationRef,
    private vcr: ViewContainerRef,
    private clipboard: Clipboard,
    private changeDetectorRef: ChangeDetectorRef
  ) {}

  private initializeTooltip(
    tooltipText: string = this.copyToClipboardTooltipText(),
    tooltipStyle: TooltipStyle = TooltipStyle.Default
  ) {
    this.clearAllTimeouts();

    if (this.componentRef != null) {
      this.componentRef.destroy();
      this.componentRef = null;
    }

    this.createTooltip(tooltipText, tooltipStyle);
    this.setShowTooltipTimeout();
  }

  private createTooltip(
    tooltipText: string = this.copyToClipboardTooltipText(),
    tooltipStyle: TooltipStyle = TooltipStyle.Default
  ): void {
    const { left, right, top } =
      this.elementRef.nativeElement.getBoundingClientRect();

    this.componentRef = this.vcr.createComponent(TooltipComponent);

    this.componentRef.setInput('tooltip', tooltipText);
    this.componentRef.setInput('style', tooltipStyle);
    this.componentRef.setInput('left', Math.round((right - left) / 2 + left));
    this.componentRef.setInput('top', Math.round(top));
  }

  private showTooltip(): void {
    if (this.componentRef != null) {
      this.componentRef.setInput('visible', true);
    }
  }

  private hideTooltip(): void {
    if (this.componentRef != null) {
      this.componentRef.setInput('visible', false);
    }
  }

  private setShowTooltipTimeout(
    delay: number = this.copyToClipboardTooltipShowDelay()
  ): void {
    const timeout: ReturnType<typeof setTimeout> = setTimeout(() => {
      this.showTooltip();
      this.changeDetectorRef.markForCheck();
    }, delay);

    this.showTimeouts.push(timeout);
  }

  private setHideTooltipTimeout(
    delay: number = this.copyToClipboardTooltipHideDelay()
  ): void {
    const timeout: ReturnType<typeof setTimeout> = setTimeout(() => {
      this.hideTooltip();
      this.changeDetectorRef.markForCheck();
    }, delay);

    this.hideTimeouts.push(timeout);
  }

  private clearAllTimeouts(): void {
    this.showTimeouts.forEach((i) => clearTimeout(i));
    this.showTimeouts = [];

    this.hideTimeouts.forEach((i) => clearTimeout(i));
    this.hideTimeouts = [];
  }

  ngOnDestroy(): void {
    this.destroy();
  }

  private destroy(): void {
    this.clearAllTimeouts();

    if (this.componentRef !== null) {
      this.appRef.detachView(this.componentRef.hostView);
      this.componentRef.destroy();
      this.componentRef = null;
    }
  }
}

import { Directive, HostBinding, input, InputSignal } from '@angular/core';

type Both = '';
export type Focusable = Both | 'keyboard' | 'mouse' | 'none';

@Directive({ selector: '[focusable]' })
export class FocusableDirective {
  focusable: InputSignal<Focusable> = input<Focusable>('');

  @HostBinding('tabIndex')
  get tabIndex(): number {
    return this.focusable() === 'keyboard' || this.focusable() === '' ? 0 : -1;
  }

  @HostBinding('style.pointer-events')
  get pointerEvents(): string {
    return this.focusable() === 'mouse' || this.focusable() === ''
      ? 'auto'
      : 'none';
  }
}

import {
  Directive,
  HostListener,
  input,
  InputSignal,
  OnInit,
  Optional
} from '@angular/core';
import { ActivatedRoute, Params, Router } from '@angular/router';
import { NgControl } from '@angular/forms';

/**
 * Use this directive when you need to pass value to query params
 *
 * Note: Works bad with [(ngModel)]
 */
@Directive({ selector: '[queryControl]', standalone: true })
export class QueryControlDirective implements OnInit {
  queryControl: InputSignal<string> = input.required<string>();
  queryEmitEvent: InputSignal<boolean> = input<boolean>(true);

  @HostListener('ngModelChange', ['$event'])
  onNgModelChange(rawValue: object | string): void {
    if (this.queryControl == null) {
      console.error('Ng Model Change! Query is not defined!');
      return;
    }

    const queryParams: Params = this.activatedRoute.snapshot.queryParams;
    const value: string =
      typeof rawValue === 'object' ? JSON.stringify(rawValue) : rawValue;

    this.router.navigate([], {
      relativeTo: this.activatedRoute,
      queryParams: { ...queryParams, [this.queryControl()]: value }
    });
  }

  constructor(
    private router: Router,
    private activatedRoute: ActivatedRoute,
    @Optional() private ngControl: NgControl
  ) {}

  ngOnInit(): void {
    if (this.ngControl == null || this.ngControl.control == null) {
      return;
    }

    const initialValue: string | undefined = this.getValueFromQuery(
      this.queryControl()
    );

    if (initialValue) {
      this.patchControlValue(initialValue);
    }
  }

  private getValueFromQuery(
    query: string | undefined,
    queryParams: Params = this.activatedRoute.snapshot.queryParams
  ): string | undefined {
    if (query == null) {
      console.error('Init Query! Query is not defined!');
      return undefined;
    }

    if (this.ngControl && this.ngControl.control) {
      this.ngControl.control.markAsTouched();
      this.ngControl.control.markAsDirty();
    }

    return queryParams[query];
  }

  private patchControlValue(value: string): void {
    if (value == null) {
      console.error('Value is not defined!');
      return;
    }

    if (this.ngControl == null || this.ngControl.control == null) {
      console.error("Control doesn't exist!");
      return;
    }

    try {
      this.ngControl.control.patchValue(JSON.parse(value), {
        emitEvent: this.queryEmitEvent
      });
    } catch (e) {
      this.ngControl.control.patchValue(value, {
        emitEvent: this.queryEmitEvent
      });
    }
  }
}

import {
  ComponentRef,
  Directive,
  input,
  InputSignal,
  OnChanges,
  SimpleChange,
  SimpleChanges,
  TemplateRef,
  ViewContainerRef
} from '@angular/core';
import { SkeletonLoaderComponent } from '../components/skeleton-loader/skeleton-loader.component';

@Directive({ selector: '[skeleton]', standalone: true })
export class SkeletonDirective implements OnChanges {
  skeleton: InputSignal<boolean> = input(false);
  skeletonWidth: InputSignal<string> = input<string>(
    `${this.random(30, 90)}px`
  );
  skeletonHeight: InputSignal<string> = input<string>('16px'); // Default text height

  constructor(
    private templateRef: TemplateRef<unknown>,
    private viewContainerRef: ViewContainerRef
  ) {}

  ngOnChanges(changes: SimpleChanges) {
    const isLoadingChange: SimpleChange = changes['skeleton'];

    if (isLoadingChange) {
      this.viewContainerRef.clear();

      if (isLoadingChange.currentValue) {
        const skeletonLoaderComponentRef: ComponentRef<SkeletonLoaderComponent> =
          this.viewContainerRef.createComponent(SkeletonLoaderComponent);

        Object.assign(skeletonLoaderComponentRef.instance, {
          width: this.skeletonWidth,
          height: this.skeletonHeight
        });
      } else {
        this.viewContainerRef.createEmbeddedView(this.templateRef);
      }
    }
  }

  private random(min: number, max: number): number {
    return Math.floor(Math.random() * (max - min + 1)) + min;
  }
}

import {
  Directive,
  ElementRef,
  input,
  InputSignal,
  OnChanges,
  Renderer2,
  SimpleChange,
  SimpleChanges
} from '@angular/core';

@Directive({ selector: '[tableLoading]', standalone: true })
export class TableLoadingDirective implements OnChanges {
  tableLoading: InputSignal<boolean> = input(false);

  private loaderRow!: HTMLElement;

  constructor(
    private elementRef: ElementRef,
    private renderer: Renderer2
  ) {}

  ngOnChanges(changes: SimpleChanges): void {
    const tableLoading: SimpleChange = changes['tableLoading'];

    if (tableLoading && tableLoading.firstChange) {
      this.initLoaderRow();
    }

    if (tableLoading && tableLoading.currentValue != null) {
      this.toggleLoadingShow(tableLoading.currentValue);
    }
  }

  private toggleLoadingShow(isLoading: boolean = false): void {
    const tableBody: unknown =
      this.elementRef?.nativeElement?.querySelector('tbody');

    if (!tableBody || this.loaderRow == null) {
      return;
    }

    if (isLoading && this.renderer) {
      this.renderer.appendChild(tableBody, this.loaderRow);
    } else {
      this.renderer.removeChild(tableBody, this.loaderRow);
    }
  }

  private initLoaderRow(): void {
    // Create Loading Row
    this.loaderRow = this.renderer.createElement('tr');
    const loaderCell: unknown = this.renderer.createElement('td');

    const countColumns: string = String(this.getCountColumns());
    this.renderer.setAttribute(loaderCell, 'colspan', countColumns);

    // Create Div For Loader
    const loaderDiv: unknown = this.renderer.createElement('div');
    this.renderer.addClass(loaderDiv, 'table-loader');
    this.renderer.setStyle(loaderDiv, 'text-align', 'center');
    this.renderer.setStyle(loaderDiv, 'padding', '20px');

    // Create Loading Text
    const loadingText: unknown = this.renderer.createText('Loading...');
    this.renderer.appendChild(loaderDiv, loadingText);
    this.renderer.appendChild(loaderCell, loaderDiv);
    this.renderer.appendChild(this.loaderRow, loaderCell);
  }

  private getCountColumns(): number {
    const tableHeader = this.elementRef.nativeElement.querySelector('thead');
    if (!tableHeader) {
      return 1; // Default to 1 if there's no header
    }

    const firstRow = tableHeader.querySelector('tr');
    return firstRow ? firstRow.children.length : 1;
  }
}

import {
  ApplicationRef,
  ChangeDetectorRef,
  ComponentRef,
  Directive,
  ElementRef,
  HostListener,
  input,
  InputSignal,
  OnDestroy,
  ViewContainerRef
} from '@angular/core';
import { TooltipComponent } from '../components/tooltip/tooltip.component';

@Directive({ selector: '[tooltip]', standalone: true })
export class TooltipDirective implements OnDestroy {
  tooltip: InputSignal<string> = input('');
  tooltipShowDelay: InputSignal<number> = input(0);
  tooltipHideDelay: InputSignal<number> = input(0);

  private componentRef: ComponentRef<any> | null = null;
  private showTimeout?: ReturnType<typeof setTimeout>;
  private hideTimeout?: ReturnType<typeof setTimeout>;

  constructor(
    private elementRef: ElementRef,
    private appRef: ApplicationRef,
    private vcr: ViewContainerRef,
    private changeDetectorRef: ChangeDetectorRef
  ) {}

  @HostListener('mouseenter')
  onMouseEnter(): void {
    this.initializeTooltip();
  }

  @HostListener('mouseleave')
  onMouseLeave(): void {
    this.setHideTooltipTimeout();
  }

  private initializeTooltip(): void {
    clearTimeout(this.showTimeout);
    clearTimeout(this.hideTimeout);

    if (this.componentRef != null) {
      this.setShowTooltipTimeout();
      return;
    }

    this.createTooltip();
    this.setShowTooltipTimeout();
  }

  private createTooltip(): void {
    const { left, right, top } =
      this.elementRef.nativeElement.getBoundingClientRect();

    this.componentRef = this.vcr.createComponent(TooltipComponent);

    this.componentRef.setInput('tooltip', this.tooltip());
    this.componentRef.setInput('left', Math.round((right - left) / 2 + left));
    this.componentRef.setInput('top', Math.round(top));
  }

  private showTooltip(): void {
    if (this.componentRef != null) {
      this.componentRef.setInput('visible', true);
    }
  }

  private hideTooltip(): void {
    if (this.componentRef != null) {
      this.componentRef.setInput('visible', false);
    }
  }

  private setShowTooltipTimeout(): void {
    this.showTimeout = setTimeout((): void => {
      this.showTooltip();
      this.changeDetectorRef.markForCheck();
    }, this.tooltipShowDelay());
  }

  private setHideTooltipTimeout(): void {
    this.hideTimeout = setTimeout((): void => {
      this.hideTooltip();
      this.changeDetectorRef.markForCheck();
    }, this.tooltipHideDelay());
  }

  ngOnDestroy(): void {
    this.destroy();
  }

  private destroy(): void {
    clearInterval(this.showTimeout);
    clearInterval(this.hideTimeout);

    if (this.componentRef !== null) {
      this.appRef.detachView(this.componentRef.hostView);
      this.componentRef.destroy();
      this.componentRef = null;
    }
  }
}

